import React, { useState } from "react";
import { View, StyleSheet } from "react-native";
import {Picker} from '@react-native-picker/picker';
import { color } from "react-native-reanimated";

const Dropdown = () => {
  const [selectedValue, setSelectedValue] = useState("");
  return (
    <View style={styles.container}>
      <Picker
        selectedValue={selectedValue}
        itemStyle = {'blue'}
        style={[{ height: '100%', width: '100%'}, styles.pick]}
        onValueChange={(itemValue) => setSelectedValue(itemValue)}
        mode ="dropdown"
        onPress
      >
        <Picker.Item label="Choose City" value="" />
        <Picker.Item label="Rawalpindi" value="Pindi" />
        <Picker.Item label="Islamabad" value="isb" />
        <Picker.Item label="Karachi" value="ka" />
        <Picker.Item label="Lahore" value="la" />
        <Picker.Item label="Peshawar" value="pe" />
        <Picker.Item label="Faisalabad" value="fa" />
        <Picker.Item label="Quetta" value="qu" />
        <Picker.Item label="Multan" value="mu" />
        <Picker.Item label="Bahawalpur" value="ba" />
      </Picker>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    width: "100%",
    height: '7%',
    alignContent: 'center',
    borderWidth: 1,
    borderRadius: 4,
    margin: 10,
    borderColor: "gray"
  },
  pick: {
      borderColor: "blue"
  }
});

export default Dropdown;
